// InformationPage.js

import React from 'react';

const InformationPage = () => {
  const boxCount = 4; // Number of boxes

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      {/* Top portion with 4 rectangle boxes and arrows */}
      <div className="flex items-center">
        {[...Array(boxCount).keys()].map((index) => (
          <div key={index} className="relative">
            <div className={`w-32 h-20 rounded-md bg-blue-500 inline-flex items-center justify-center`}>
              Box {index + 1}
            </div>
            {index < boxCount - 1 && (
              <div className={`absolute top-1/2 left-full transform -translate-y-1/2 -translate-x-4`}>
                {/* Arrow with wider and shorter tail */}
                <div className={`w-10 h-4 bg-blue-500`}></div>
                <div className={`w-2 h-6 bg-blue-500 absolute top-1/2 left-full transform -translate-y-1/2`}></div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Form section below the boxes */}
      <div className="mt-8">
        <form className="bg-white p-6 shadow-md rounded-md">
          {/* Your form fields go here */}
          <label className="block mb-2 text-sm font-semibold text-gray-600">
            Name:
            <input
              type="text"
              className="w-full p-2 border rounded-md mt-1"
              placeholder="Enter your name"
            />
          </label>
          
          {/* Add more form fields as needed */}
          
          <button
            type="submit"
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default InformationPage;
