import React from "react";
import SelectField from "./SelectField";

const FlowBox = ({ formData, handleInputChange }) => {

  const handleSelectChange = (fieldName, e) => {
    // Assuming you have a function to handle changes in your parent component
    handleInputChange(fieldName, e.target.value);
  };

  const options = [
    { value: "option1", label: "Option 1" },
    { value: "option2", label: "Option 2" },
    { value: "option3", label: "Option 3" },
  ];
  return (
    <div  className="bg-white p-4 rounded-md max-w-md mx-auto shadow-lg border-b mt-10">
      <div className="px-8 py-8">
        <SelectField label="Forward" name="forward" id='forwardid' width="w-50" options={options} fieldfunction={(e) => handleSelectChange('field', e)} value={formData.forward || ''}/>
        <SelectField label="Return" name="return" id='returnid' width="w-50" options={options} fieldfunction={(e) => handleSelectChange('field', e)} value={formData.return || ''}/>
      </div>
    </div>
  );
};

export default FlowBox;
