import React, { useState } from "react";
import FlowBox from '../components/FlowBox';
import axios from 'axios';


const FlowListing = () => {

  const [formDataArray, setFormDataArray] = useState([]);

  const handleInputChange = (sectionIndex, fieldName, value) => {
    setFormDataArray((prevData) => {
      const newData = [...prevData];
      newData[sectionIndex] = { ...newData[sectionIndex], [fieldName]: value };
      return newData;
    });
  };

  const handleAddSection = () => {
    setFormDataArray((prevData) => [...prevData, {}]);
  };

  const handleSubmit = () => {
    // Combine data from all sections
    const combinedFormData = formDataArray.reduce((acc, sectionData, index) => {
      const sectionKeyPrefix = `section${index + 1}`;
      Object.keys(sectionData).forEach((field) => {
        acc[`${sectionKeyPrefix}_${field}`] = sectionData[field];
      });
      return acc;
    }, {});

    // Use Axios to submit the form data
    axios.post('your_api_endpoint', combinedFormData)
      .then((response) => {
        console.log('Form submitted successfully', response.data);
      })
      .catch((error) => {
        console.error('Error submitting form', error);
      });
  };

  return (
    <div className="container mx-auto p-4">
      {/* <h1 className="text-3xl font-bold mb-4">Listing Page</h1>
      <button onClick={handleTopButtonClick} style={{ display: hideButton === 'hidden' ? 'none' : ' ' }}> 
      <svg class="h-8 w-8 text-red-500"  fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
      </svg>
       </button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {components}
      <button onClick={handleButtonClick} style={{ float: buttonPosition === 'right' ? 'right' : 'left',display: hideButton === 'hidden' ? ' ' : 'none'}}>
      <svg class="h-8 w-8 text-red-500"  fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
      </svg>
      </button>
      </div> */}

      

      {formDataArray.map((formData, index) => (
        <div key={index}>
          <h2>Form Section {index + 1}</h2>
          <FlowBox
            formData={formData}
            handleInputChange={(fieldName, value) => handleInputChange(index, fieldName, value)}
          />
        </div>
      ))}
      <button onClick={handleAddSection}>Add Form Section</button>
      <button onClick={handleSubmit}>Submit</button>




      {/* <button onClick={handleSaveClick} >Save</button> */}
    </div>
  );
};

export default FlowListing;
