import React, { useState } from "react";
import TextField from "../components/TextField";
import TaskService from "../services/TaskService";
import ButtonField from "../components/ButtonField";
import DateField from "../components/DateField";

const TaskAddPage = (props) => {
  const [task, setTask] = useState({
    id: "",
    taskName: "",
    completionDate: "",
    assignDate: "",
  });

  const handleChange = (e) => {
    const value = e.target.value;
    setTask({ ...task, [e.target.name]: value });
  };

  const saveTask = (e) => {
    e.preventDefault();
    // TaskService.saveTask(task)
    //   .then((response) => {
    //     console.log(response);
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //   });
    props.onClose
  };
  return (
    <div className="flex max-w-2xl mx-auto shadow border-b">
      <div className="px-8 py-8">
        <div className="font-thin text-2xl tracking-wider">
          <h1>Add Task</h1>
        </div>
        <TextField label="TaskName" name="taskName" id="taskNameid" value={task.taskName} fieldfunction={(e) => handleChange(e)}/>
        <DateField label="CompletionDate" name="completionDate" id="completionDateid" fieldfunction={(e) => handleChange(e)}/>
        <DateField label="AssignDate" name="assignDate" id="assignDateid" fieldfunction={(e) => handleChange(e)}/>
        <ButtonField id="saveId" value="Save" buttonfunction={saveTask}/>
        <button onClick={props.onSave}>Save</button>
        <button onClick={props.onClose}>Close</button>
      </div>
    </div>
  );
};

export default TaskAddPage;
