import React from 'react';

const InformationPage = () => {
  return (
    <div>
      <div style={topPortionStyles}>
        <BoxWithArrow>Box 1</BoxWithArrow>
        <BoxWithArrow>Box 2</BoxWithArrow>
        <BoxWithArrow>Box 3</BoxWithArrow>
        <BoxWithArrow>Box 4</BoxWithArrow>
      </div>

      <div style={formStyles}>
        <form>
          {/* Your form elements go here */}
          <label>
            Name:
            <input type="text" name="name" />
          </label>
          {/* Add more form elements as needed */}
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

// Styles for the top portion (containing boxes and arrows)
const topPortionStyles = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  padding: '20px',
};

// Styles for individual elliptical boxes with arrows
const boxWithArrowStyles = {
  display: 'flex',
  alignItems: 'center',
};

// Styles for individual elliptical boxes
const ellipseBoxStyles = {
  width: '100px',
  height: '50px',
  borderRadius: '50%',
  background: '#3498db', // Change the background color as needed
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  color: '#fff', // Change the text color as needed
};

// Styles for the arrow container
const arrowContainerStyles = {
  position: 'relative',
};

// CSS class for the arrow
const arrowStyles = {
  height: '2px',
  width: '40px',
  background: '#3498db', // Change the arrow color as needed
  position: 'absolute',
  top: '50%',
  transform: 'translateY(-50%)',
};

// Styles for arrowheads
const arrowheadStyles = {
  position: 'absolute',
  width: '0',
  height: '0',
  borderLeft: '8px solid transparent',
  borderRight: '8px solid transparent',
};

// Arrow component
const Arrow = () => (
  <div style={arrowContainerStyles}>
    <div style={arrowStyles}>
      <div style={{ ...arrowheadStyles, borderRight: '8px solid #3498db' }}></div>
      <div style={{ ...arrowheadStyles, borderLeft: '8px solid #3498db', right: '0' }}></div>
    </div>
  </div>
);

// Box component with arrow
const BoxWithArrow = ({ children }) => (
  <div style={boxWithArrowStyles}>
    <div style={ellipseBoxStyles}>{children}</div>
    <Arrow />
  </div>
);

// Styles for the form section
const formStyles = {
  padding: '20px',
};

export default InformationPage;
