import React, { useState } from "react";
import FlowBox from '../components/FlowBox';


const FlowListing = () => {

  const [components, setComponents] = useState([]);

  const [buttonPosition, setButtonPosition] = useState('right');

  const [hideButton, setHideButton] = useState('show');

  const handleButtonClick = () => {
    const newContent = `${components.length + 1}`;
    setComponents([...components, <FlowBox fieldfunction={(e) => handleInputChange(e)} key={components.length} content={newContent}/>]);

    setButtonPosition('left');

  };
  const handleTopButtonClick = () => {

    handleButtonClick();
   
    setHideButton('hidden');

  };

  const [formData, setFormData] = useState({
    forward: '',
    return: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Listing Page</h1>
      <button onClick={handleTopButtonClick} style={{ display: hideButton === 'hidden' ? 'none' : ' ' }}> 
      <svg class="h-8 w-8 text-red-500"  fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
      </svg>
       </button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {components}
      <button onClick={handleButtonClick} style={{ float: buttonPosition === 'right' ? 'right' : 'left',display: hideButton === 'hidden' ? ' ' : 'none'}}>
      <svg class="h-8 w-8 text-red-500"  fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"/>
      </svg>
      </button>
      </div>

      <button onClick={handleButtonClick} >Save</button>
    </div>
  );
};

export default FlowListing;
