import React, { useState } from "react";
import FlowBox from '../components/FlowBox';


const FlowListing = () => {

  const [components, setComponents] = useState([]);

  const [buttonPosition, setButtonPosition] = useState('right');

  const [hideButton, setHideButton] = useState('show');

  const handleButtonClick = () => {
    const newContent = `This is component ${components.length + 1}`;
    setComponents([...components, <FlowBox key={components.length} content={newContent} />]);

    setButtonPosition('left');
  };
  const handleTopButtonClick = () => {
   
    setHideButton('hidden');
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Listing Page</h1>
      <button onClick={handleTopButtonClick} className={{ float: hideButton === 'show' ? 'a' : 'hidden' }}>Add New Component</button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {components}
      <button onClick={handleButtonClick} style={{ float: buttonPosition === 'right' ? 'right' : 'left' }}>Add New Component</button>
      </div>
    </div>
  );
};

export default FlowListing;
