import React, { useState } from "react";
import TextField from "../components/TextField"
import TaskService from "../services/TaskService";

const TaskAddPage = (props) => {

  const [task, setTask] = useState({
    id:"",
    taskName:"",
    completionDate:"",
    assignDate:""
  });

  const handleChange=(e)=>{
    const value=e.target.value;
    setTask({...task,[e.target.taskName]:value})
  };
  

  const saveTask=(e)=>{
    e.preventDefault();
    TaskService.saveTask(task).then((response)=>{
      console.log(response);
    }).catch((error)=>{
      console.log(error);
    });

  };
  return (
    <div className="flex max-w-2xl mx-auto shadow border-b">
      <div className="px-8 py-8">
        <div className="font-thin text-2xl tracking-wider">
          <h1>Add New Employee</h1>
        </div>
        <TextField label="TaskName" name="taskName" id="taskNameid" value={task.taskName}  />

        <div className="items-center justify-center h-14 w-full my-4 space-x-4 pt-4">
          <button onClick={saveTask} className="rounded text-white font-semibold bg-green-400 hover:bg-green-700 py-2 px-6">
            Save
          </button>
          <button className="rounded text-white font-semibold bg-red-400 hover:bg-red-700 py-2 px-6">
            Clear
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskAddPage;
