import "./App.css";
import "./styles.css";
import { BrowserRouter as Router, Route, Routes  } from 'react-router-dom';
import React, { useState } from 'react';
import CompletedListing from "./page/CompletedListing";
import Layout from "./page/Layout";
import FileStatusListingPage from "./page/FileStatusListingPage";
import FlowListing from "./pagecomponents/FlowListing";
import ForwardedListing from "./page/ForwardedListing";
import LoginPage from "./page/LoginPage";

function App() {

  const [isLoggedIn , setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  return (


<Router>
{isLoggedIn ? (
  <Layout>
    <Route path="/completed">
      <CompletedListing />
    </Route>
    <Route path="/formwarded">
      <ForwardedListing />
    </Route>
    <Route path="/" exact>
      <FileStatusListingPage />
    </Route>
  </Layout>
) : (
  <LoginPage onLogin={handleLogin} />
)}
</Router>
  );
}

export default App;
