import React, { useEffect, useState } from 'react';
import TaskService from '../services/TaskService';
import TaskAddPage from './TaskAddPage';

const PopupComponent = ({ onSave, onClose }) => {
  // Your popup component code goes here

  return (
    <div>
      {/* Popup content */}
      <TaskAddPage/>
      <button onClick={onSave}>Save</button>
      <button onClick={onClose}>Close</button>
    </div>
  );
};

const FileStatusListingPage = () => {
  const [data, setData] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const handleSave = () => {
    // Handle saving logic
    // You can update data or perform any other action here
    closePopup();
  };

  useEffect(() => {
    TaskService.getFiles()
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">File Listing Page</h1>
        <button className="bg-blue-500 text-white py-2 px-4 rounded-md" onClick={openPopup}>
          Open Popup
        </button>
      </div>

      {/* Render the modal if isPopupOpen is true */}
      {isPopupOpen && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center">
          <TaskAddPage onSave={handleSave} onClose={closePopup} />
        </div>
      )}

      <table className="min-w-full bg-white border border-gray-300 shadow-md rounded-md">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">Name</th>
            <th className="py-2 px-4 border-b">Current Flow</th>
            <th className="py-2 px-4 border-b">Type</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td className="py-2 px-4 border-b">{item.name}</td>
              <td className="py-2 px-4 border-b">{item.currentFlow}</td>
              <td className="py-2 px-4 border-b">{item.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FileStatusListingPage;
