import React from "react";
import TextField from "./TextField";
import TextAreaField from "./TextAreaField";
import NumberField from "./NumberField";
import DateField from "./DateField";
import SelectField from "./SelectField";
import ButtonField from "./ButtonField";

const FlowBox = (props) => {

  const options = [
    { value: "option1", label: "Option 1" },
    { value: "option2", label: "Option 2" },
    { value: "option3", label: "Option 3" },
  ];
  return (
    <div className="flex max-w-2xl mx-auto shadow border-b">
      <div className="px-8 py-8">
        <div className="font-thin text-2xl tracking-wider">
          <h1>Add New Employee</h1>
        </div>
        <TextField label="Name" name="name" id="nameid" />
        <NumberField label="Age" name="age" id="ageid" />
        <DateField label="DOB" name="dob" id="dobid" />
        <TextAreaField label="Address" name="address" id="addressid" />
        <div className="items-center justify-center h-14 w-full my-4 space-x-4 pt-4">
          <button className="rounded text-white font-semibold bg-green-400 hover:bg-green-700 py-2 px-6">
            Save
          </button>
          <button className="rounded text-white font-semibold bg-red-400 hover:bg-red-700 py-2 px-6">
            Clear
          </button>
        </div>
      </div>
    </div>
  );
};

export default FlowBox;
